package work.datetype;

public class Nightmult_Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for (int i = 2; i <10; i++) {
			for (int j = 2; j < 10; j++) {
				System.out.print(i+""+"*"+j+""+"="+""+i*j+"\t");
			}
			System.out.println();
		}
	}

}
